package main

import (
	"fmt"
)

// 接口可以让函数更抽象
type Blunt interface {
	Beat() string
}

type Hammer struct {
}

func (h Hammer) Beat() string {
	return "duang!!!"
}

type Wrench struct {
}

func (w Wrench) Beat() string {
	return "pang!!!"
}

func BluntSound(b Blunt) {
	fmt.Println(b.Beat())
}

